import React, { useState, useEffect } from "react";
import Layout from "../components/Layout";

const Dashboard = () => {
  const [userOrganizations, setUserOrganizations] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [accessRequests, setAccessRequests] = useState([]);
  const [newOrg, setNewOrg] = useState({
    name: "",
    address: "",
    contactNumber: "",
    email: "",
    description: "",
    registrationId: "",
    industry: "",
    website: "",
  });
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const token = localStorage.getItem("token");

  // Fetch user's organizations
  useEffect(() => {
    const fetchOrganizations = async () => {
      try {
        const response = await fetch("http://localhost:4000/api/organizations", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        const data = await response.json();
        if (response.ok) {
          setUserOrganizations(data.organizations);
        } else {
          setError(data.message || "Failed to fetch organizations.");
        }
      } catch (error) {
        console.error("Error fetching organizations:", error);
        setError("Something went wrong. Please try again later.");
      }
    };

    const fetchAccessRequests = async () => {
      // Fetch user's access requests
      try {
        const response = await fetch(
          "http://localhost:4000/api/access-requests",
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        const data = await response.json();
        if (response.ok) {
          setAccessRequests(data.requests);
        } else {
          setError(data.message || "Failed to fetch access requests.");
        }
      } catch (error) {
        console.error("Error fetching access requests:", error);
        setError("Something went wrong. Please try again later.");
      }
    };

    fetchOrganizations();
    fetchAccessRequests();
  }, [token]);

  // Search for organizations
  const handleSearch = async () => {
    setError("");
    if (!searchTerm) return;

    try {
      const response = await fetch(
        `http://localhost:4000/api/organizations/search?name=${searchTerm}`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      const data = await response.json();
      if (response.ok) {
        setSearchResults(data.organizations);
      } else {
        setError(data.message || "Failed to search organizations.");
      }
    } catch (error) {
      console.error("Error searching organizations:", error);
      setError("Something went wrong. Please try again later.");
    }
  };

  // Request access to an organization
  const handleRequestAccess = async (orgId) => {
    try {
      const response = await fetch(
        `http://localhost:4000/api/organizations/${orgId}/request-access`,
        {
          method: "POST",
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      const data = await response.json();
      if (response.ok) {
        alert("Access request submitted successfully!");
        setAccessRequests((prev) => [...prev, data.request]);
      } else {
        setError(data.message || "Failed to request access.");
      }
    } catch (error) {
      console.error("Error requesting access:", error);
      setError("Something went wrong. Please try again later.");
    }
  };

  // Create a new organization
  const handleCreateOrganization = async () => {
    setError("");
    setLoading(true);

    if (!newOrg.name) {
      setError("Organization name is required.");
      setLoading(false);
      return;
    }

    try {
      const response = await fetch("http://localhost:4000/api/organizations", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(newOrg),
      });
      const data = await response.json();
      if (response.ok) {
        alert("Organization created successfully!");
        setUserOrganizations((prev) => [...prev, data.organization]);
        setNewOrg({
          name: "",
          address: "",
          contactNumber: "",
          email: "",
          description: "",
          registrationId: "",
          industry: "",
          website: "",
        });
      } else {
        setError(data.message || "Failed to create organization.");
      }
    } catch (error) {
      console.error("Error creating organization:", error);
      setError("Something went wrong. Please try again later.");
    }
    setLoading(false);
  };

  return (
    <Layout>
      <div className="p-8 space-y-8">
        <h1 className="text-3xl font-bold text-indigo-600">Dashboard</h1>

        {/* User's Organizations */}
        <section>
          <h2 className="text-xl font-semibold text-gray-700 mb-4">
            Your Organizations
          </h2>
          <ul className="space-y-4">
            {userOrganizations.length > 0 ? (
              userOrganizations.map((org) => (
                <li key={org._id} className="p-4 border rounded shadow">
                  <h3 className="text-lg font-bold">{org.name}</h3>
                  <p className="text-gray-600">{org.description || "No description provided."}</p>
                </li>
              ))
            ) : (
              <p className="text-gray-500">You are not part of any organizations yet.</p>
            )}
          </ul>
        </section>

        {/* Search and Request Access */}
        <section>
          <h2 className="text-xl font-semibold text-gray-700 mb-4">
            Search for Organizations
          </h2>
          <div className="flex items-center gap-4 mb-4">
            <input
              type="text"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="flex-1 px-4 py-2 border rounded shadow-sm"
              placeholder="Enter organization name"
            />
            <button
              onClick={handleSearch}
              className="bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700 transition"
            >
              Search
            </button>
          </div>
          <ul className="space-y-4">
            {searchResults.length > 0 ? (
              searchResults.map((org) => (
                <li key={org._id} className="p-4 border rounded shadow">
                  <h3 className="text-lg font-bold">{org.name}</h3>
                  <p className="text-gray-600">{org.description || "No description provided."}</p>
                  <button
                    onClick={() => handleRequestAccess(org._id)}
                    className="mt-2 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
                  >
                    Request Access
                  </button>
                </li>
              ))
            ) : (
              <p className="text-gray-500">No organizations found.</p>
            )}
          </ul>
        </section>

        {/* Access Requests */}
        <section>
          <h2 className="text-xl font-semibold text-gray-700 mb-4">
            Access Requests
          </h2>
          <ul className="space-y-4">
            {accessRequests.length > 0 ? (
              accessRequests.map((request) => (
                <li key={request._id} className="p-4 border rounded shadow">
                  <p className="text-gray-600">Request for: {request.orgId.name}</p>
                  <p className="text-gray-500">Status: {request.status}</p>
                </li>
              ))
            ) : (
              <p className="text-gray-500">No access requests yet.</p>
            )}
          </ul>
        </section>

        {/* Create Organization */}
        <section>
          <h2 className="text-xl font-semibold text-gray-700 mb-4">
            Create New Organization
          </h2>
          <div className="space-y-4">
            <input
              type="text"
              value={newOrg.name}
              onChange={(e) => setNewOrg({ ...newOrg, name: e.target.value })}
              className="w-full px-4 py-2 border rounded shadow-sm"
              placeholder="Organization Name"
            />
            {/* Additional fields can be added here */}
            <button
              onClick={handleCreateOrganization}
              className="w-full bg-green-600 text-white py-2 rounded hover:bg-green-700 transition"
              disabled={loading}
            >
              {loading ? "Creating..." : "Create Organization"}
            </button>
          </div>
        </section>
      </div>
    </Layout>
  );
};

export default Dashboard;
