import React, { useEffect, useState } from "react";
import Layout from "../components/Layout";

const OrgHomePage = () => {
  // Mock organization data for display
  const [organization, setOrganization] = useState(null);
  const [error, setError] = useState("");

  useEffect(() => {
    // Mock API call for fetching organization details
    const fetchOrganizationDetails = () => {
      try {
        const mockOrganization = {
          name: "Test Organization",
          address: "123 Main Street, City, Country",
          contactNumber: "+1 (123) 456-7890",
          email: "info@testorg.com",
          description: "This is a test organization description.",
          industry: "Technology",
          website: "https://www.testorg.com",
          registrationId: "REG123456",
          isAdmin: true, // Mock admin status
        };

        setOrganization(mockOrganization);
      } catch (error) {
        console.error("Error fetching organization details:", error);
        setError("Something went wrong. Please try again later.");
      }
    };

    fetchOrganizationDetails();
  }, []);

  if (error) {
    return (
      <Layout>
        <p className="text-red-500 text-center bg-red-100 p-3 rounded">
          {error}
        </p>
      </Layout>
    );
  }

  if (!organization) {
    return (
      <Layout>
        <p className="text-gray-500 text-center">Loading...</p>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className="min-h-screen bg-gradient-to-r from-blue-500 to-purple-500 p-10 text-white">
        <div className="max-w-5xl mx-auto bg-white p-8 rounded-lg shadow-lg text-gray-800">
          <h1 className="text-4xl font-bold mb-6 text-center">
            {organization.name}
          </h1>
          <div className="space-y-4 text-lg">
            <p>
              <strong>Address:</strong> {organization.address || "N/A"}
            </p>
            <p>
              <strong>Contact Number:</strong>{" "}
              {organization.contactNumber || "N/A"}
            </p>
            <p>
              <strong>Email:</strong> {organization.email || "N/A"}
            </p>
            <p>
              <strong>Description:</strong>{" "}
              {organization.description || "No description available."}
            </p>
            <p>
              <strong>Industry:</strong> {organization.industry || "N/A"}
            </p>
            <p>
              <strong>Website:</strong>{" "}
              <a
                href={organization.website}
                target="_blank"
                rel="noopener noreferrer"
                className="text-blue-600 hover:underline"
              >
                {organization.website || "N/A"}
              </a>
            </p>
            <p>
              <strong>Registration ID:</strong>{" "}
              {organization.registrationId || "N/A"}
            </p>
          </div>
          {organization.isAdmin && (
            <div className="mt-6 text-center">
              <button
                className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700 transition font-semibold"
                onClick={() => alert("Navigate to Admin Controls")}
              >
                Admin Controls
              </button>
            </div>
          )}
        </div>
      </div>
    </Layout>
  );
};

export default OrgHomePage;
