// src/pages/FinanceManagement.jsx

import React, { useState, useEffect } from "react";
import Layout from "../components/Layout";
import Modal from "../components/Modal";
import axios from "axios";

const FinanceManagement = () => {
  const [activeTab, setActiveTab] = useState("categories");
  const [categories, setCategories] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [categoryForm, setCategoryForm] = useState({
    name: "",
    description: "",
    parentCategoryId: "",
  });
  const [editCategoryId, setEditCategoryId] = useState(null);

  const orgId = localStorage.getItem("orgId"); // Fetch the orgId dynamically
  const token = localStorage.getItem("token");

  // Fetch categories from the backend
  useEffect(() => {
    if (activeTab === "categories") fetchCategories();
  }, [activeTab]);

  const fetchCategories = async () => {
    try {
      const response = await axios.get(
        `http://localhost:4000/api/organizations/${orgId}/components/finance/categories`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setCategories(response.data.categories);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  };

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setCategoryForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleSaveCategory = async () => {
    try {
      const url = editCategoryId
        ? `http://localhost:4000/api/organizations/${orgId}/components/finance/categories/${editCategoryId}`
        : `http://localhost:4000/api/organizations/${orgId}/components/finance/categories`;
      const method = editCategoryId ? "put" : "post";

      await axios[method](url, categoryForm, {
        headers: { Authorization: `Bearer ${token}` },
      });

      fetchCategories();
      setShowModal(false);
      setCategoryForm({ name: "", description: "", parentCategoryId: "" });
      setEditCategoryId(null);
    } catch (error) {
      console.error("Error saving category:", error);
    }
  };

  const handleEditCategory = (category) => {
    setEditCategoryId(category._id);
    setCategoryForm({
      name: category.name,
      description: category.description,
      parentCategoryId: category.parentCategoryId || "",
    });
    setShowModal(true);
  };

  const handleDeleteCategory = async (categoryId) => {
    try {
      await axios.delete(
        `http://localhost:4000/api/organizations/${orgId}/components/finance/categories/${categoryId}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      fetchCategories();
    } catch (error) {
      console.error("Error deleting category:", error);
    }
  };

  // Render content for the active tab
  const renderTabContent = () => {
    switch (activeTab) {
      case "categories":
        return (
          <div>
            <h2 className="text-2xl font-semibold mb-4">Manage Categories</h2>
            <button
              onClick={() => setShowModal(true)}
              className="bg-blue-600 text-white px-4 py-2 rounded-lg mb-4"
            >
              Add Category
            </button>
            <div className="space-y-4">
              {categories.map((category) => (
                <div
                  key={category._id}
                  className="p-4 bg-gray-100 rounded-lg flex justify-between items-center"
                >
                  <div>
                    <p className="text-lg font-semibold">{category.name}</p>
                    <p className="text-sm text-gray-600">
                      {category.description || "No description provided"}
                    </p>
                  </div>
                  <div className="flex space-x-2">
                    <button
                      onClick={() => handleEditCategory(category)}
                      className="bg-yellow-500 text-white px-3 py-1 rounded"
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDeleteCategory(category._id)}
                      className="bg-red-500 text-white px-3 py-1 rounded"
                    >
                      Delete
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        );
      case "fields":
        return (
          <div>
            <h2 className="text-2xl font-semibold mb-4">Manage Fields</h2>
            <p>Field management will go here.</p>
          </div>
        );
      case "records":
        return (
          <div>
            <h2 className="text-2xl font-semibold mb-4">Manage Records</h2>
            <p>Record management will go here.</p>
          </div>
        );
      case "templates":
        return (
          <div>
            <h2 className="text-2xl font-semibold mb-4">Manage Templates</h2>
            <p>Template management will go here.</p>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <Layout>
      <div className="min-h-screen bg-gray-100 p-6">
        <div className="max-w-5xl mx-auto bg-white shadow-lg rounded-lg">
          <div className="p-6">
            <h1 className="text-3xl font-bold mb-6">Finance Management</h1>
            <div className="border-b border-gray-200">
              <nav className="flex space-x-4">
                <button
                  className={`py-2 px-4 font-medium ${
                    activeTab === "categories"
                      ? "text-blue-600 border-b-2 border-blue-600"
                      : "text-gray-600 hover:text-blue-600"
                  }`}
                  onClick={() => setActiveTab("categories")}
                >
                  Categories
                </button>
                <button
                  className={`py-2 px-4 font-medium ${
                    activeTab === "fields"
                      ? "text-blue-600 border-b-2 border-blue-600"
                      : "text-gray-600 hover:text-blue-600"
                  }`}
                  onClick={() => setActiveTab("fields")}
                >
                  Fields
                </button>
                <button
                  className={`py-2 px-4 font-medium ${
                    activeTab === "records"
                      ? "text-blue-600 border-b-2 border-blue-600"
                      : "text-gray-600 hover:text-blue-600"
                  }`}
                  onClick={() => setActiveTab("records")}
                >
                  Records
                </button>
                <button
                  className={`py-2 px-4 font-medium ${
                    activeTab === "templates"
                      ? "text-blue-600 border-b-2 border-blue-600"
                      : "text-gray-600 hover:text-blue-600"
                  }`}
                  onClick={() => setActiveTab("templates")}
                >
                  Templates
                </button>
              </nav>
            </div>
            <div className="p-6">{renderTabContent()}</div>
          </div>
        </div>
      </div>

      {/* Modal for creating/editing categories */}
      {showModal && (
        <Modal onClose={() => setShowModal(false)}>
          <div>
            <h2 className="text-xl font-semibold mb-4">
              {editCategoryId ? "Edit Category" : "Add Category"}
            </h2>
            <div className="space-y-4">
              <input
                type="text"
                name="name"
                value={categoryForm.name}
                onChange={handleFormChange}
                placeholder="Category Name"
                className="w-full px-4 py-2 border rounded-lg"
              />
              <textarea
                name="description"
                value={categoryForm.description}
                onChange={handleFormChange}
                placeholder="Category Description"
                className="w-full px-4 py-2 border rounded-lg"
              />
              <button
                onClick={handleSaveCategory}
                className="bg-blue-600 text-white px-4 py-2 rounded-lg"
              >
                Save
              </button>
            </div>
          </div>
        </Modal>
      )}
    </Layout>
  );
};

export default FinanceManagement;
