// src/pages/RecordModal.jsx
import React, { useState, useEffect } from "react";
import Modal from "../components/Modal";

const RecordModal = ({ onClose, onSave, categories = [], fields = [] }) => {
  const [error, setError] = useState("");
  const [type, setType] = useState("revenue");
  const [categoryId, setCategoryId] = useState("");
  const [fieldValues, setFieldValues] = useState({});
  const [isRecurring, setIsRecurring] = useState(false);
  const [recurrenceFrequency, setRecurrenceFrequency] = useState("monthly");
  const [isPartialPayment, setIsPartialPayment] = useState(false);
  const [totalAmount, setTotalAmount] = useState("");
  const [amountPaidOrReceived, setAmountPaidOrReceived] = useState("");

  useEffect(() => {
    const initialValues = {};
    (fields || []).forEach(field => {
      if (field.type === "dropdown" && field.options.length > 0) {
        initialValues[field.name] = field.options[0];
      } else if (field.type === "boolean") {
        initialValues[field.name] = false;
      } else {
        initialValues[field.name] = "";
      }
    });
    setFieldValues(initialValues);
  }, [fields]);

  const handleSave = () => {
    if (!type) {
      setError("Please select a record type.");
      return;
    }
    if (!categoryId) {
      setError("Please select a category.");
      return;
    }

    if (isPartialPayment) {
      if (!totalAmount || isNaN(totalAmount) || Number(totalAmount) <= 0) {
        setError("Please enter a valid total amount for partial payment.");
        return;
      }
      if (!amountPaidOrReceived || isNaN(amountPaidOrReceived) || Number(amountPaidOrReceived) < 0) {
        setError("Please enter a valid amount paid/received so far.");
        return;
      }
      if (Number(amountPaidOrReceived) > Number(totalAmount)) {
        setError("Amount paid/received cannot exceed the total amount.");
        return;
      }
    }

    const recordData = {
      type,
      categoryId,
      fields: { ...fieldValues },
      recurrence: isRecurring ? { frequency: recurrenceFrequency } : {},
      status: "draft",
    };

    if (isPartialPayment) {
      recordData.fields["total_amount"] = totalAmount;
      recordData.fields["amount_paid"] = amountPaidOrReceived;
    }

    onSave(recordData);
  };

  const handleFieldChange = (field, value) => {
    setFieldValues(prev => ({ ...prev, [field.name]: value }));
  };

  const renderFieldInput = (field) => {
    const value = fieldValues[field.name] || "";
    switch (field.type) {
      case "string":
        return (
          <input
            type="text"
            className="w-full p-2 border border-gray-300 rounded focus:ring-2 focus:ring-blue-400"
            value={value}
            onChange={(e) => handleFieldChange(field, e.target.value)}
          />
        );
      case "number":
        return (
          <input
            type="number"
            className="w-full p-2 border border-gray-300 rounded focus:ring-2 focus:ring-blue-400"
            value={value}
            onChange={(e) => handleFieldChange(field, e.target.value)}
          />
        );
      case "date":
        return (
          <input
            type="date"
            className="w-full p-2 border border-gray-300 rounded focus:ring-2 focus:ring-blue-400"
            value={value}
            onChange={(e) => handleFieldChange(field, e.target.value)}
          />
        );
      case "dropdown":
        return (
          <select
            className="w-full p-2 border border-gray-300 rounded focus:ring-2 focus:ring-blue-400"
            value={value}
            onChange={(e) => handleFieldChange(field, e.target.value)}
          >
            {field.options.map(opt => <option key={opt} value={opt}>{opt}</option>)}
          </select>
        );
      case "formula":
        return (
          <input
            type="text"
            className="w-full p-2 border border-gray-300 rounded bg-gray-100 text-gray-700"
            value={value}
            readOnly
          />
        );
      case "boolean":
        return (
          <input
            type="checkbox"
            checked={!!value}
            onChange={(e) => handleFieldChange(field, e.target.checked)}
          />
        );
      default:
        return null;
    }
  };

  return (
    <Modal onClose={onClose}>
      <div className="p-6 bg-white rounded-lg shadow-lg max-w-lg mx-auto text-black z-50 relative max-h-[80vh] overflow-y-auto">
        <h2 className="text-2xl font-bold mb-4 text-gray-900">Add New Record</h2>
        {error && <p className="text-red-600 mb-4 text-sm">{error}</p>}

        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-900 mb-1">Record Type</label>
            <select
              value={type}
              onChange={(e) => setType(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded focus:ring-2 focus:ring-blue-400"
            >
              <option value="revenue">Revenue</option>
              <option value="expense">Expense</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-900 mb-1">Category</label>
            <select
              value={categoryId}
              onChange={(e) => setCategoryId(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded focus:ring-2 focus:ring-blue-400"
            >
              <option value="">-- Select a Category --</option>
              {categories && categories.map(cat => (
                <option key={cat._id} value={cat._id}>{cat.name}</option>
              ))}
            </select>
          </div>

          {fields && fields.length > 0 && (
            <div className="bg-gray-50 p-4 rounded border border-gray-200 space-y-4">
              <h3 className="text-sm font-semibold mb-2">Additional Fields</h3>
              {fields.map(field => (
                <div key={field._id}>
                  <label className="block text-sm font-medium text-gray-900 mb-1">
                    {field.label || field.name} 
                    {field.type === "formula" && " (Auto-calculated)"}
                  </label>
                  {renderFieldInput(field)}
                </div>
              ))}
            </div>
          )}

          <div className="bg-blue-50 p-4 rounded border border-blue-200 space-y-2">
            <label className="block text-sm font-medium text-gray-900">
              <input
                type="checkbox"
                className="mr-2"
                checked={isRecurring}
                onChange={(e) => setIsRecurring(e.target.checked)}
              />
              Is Recurring?
            </label>
            {isRecurring && (
              <div>
                <label className="block text-sm text-gray-900 mb-1">Frequency</label>
                <select
                  value={recurrenceFrequency}
                  onChange={(e) => setRecurrenceFrequency(e.target.value)}
                  className="w-full p-2 border border-gray-300 rounded"
                >
                  <option value="daily">Daily</option>
                  <option value="weekly">Weekly</option>
                  <option value="monthly">Monthly</option>
                </select>
              </div>
            )}
          </div>

          <div className="bg-yellow-50 p-4 rounded border border-yellow-200 space-y-2">
            <label className="block text-sm font-medium text-gray-900">
              <input
                type="checkbox"
                className="mr-2"
                checked={isPartialPayment}
                onChange={(e) => setIsPartialPayment(e.target.checked)}
              />
              Partial Payment/Receivable?
            </label>
            {isPartialPayment && (
              <div className="space-y-2">
                <div>
                  <label className="block text-sm text-gray-900 mb-1">Total Amount</label>
                  <input
                    type="number"
                    className="w-full p-2 border border-gray-300 rounded"
                    value={totalAmount}
                    onChange={(e) => setTotalAmount(e.target.value)}
                  />
                </div>
                <div>
                  <label className="block text-sm text-gray-900 mb-1">Amount Paid/Received So Far</label>
                  <input
                    type="number"
                    className="w-full p-2 border border-gray-300 rounded"
                    value={amountPaidOrReceived}
                    onChange={(e) => setAmountPaidOrReceived(e.target.value)}
                  />
                </div>
              </div>
            )}
          </div>
        </div>

        <div className="flex justify-end mt-6 space-x-3 border-t border-gray-200 pt-4">
          <button
            className="bg-gray-300 text-gray-800 px-4 py-2 rounded hover:bg-gray-400 transition"
            onClick={onClose}
          >
            Cancel
          </button>
          <button
            className="bg-green-600 text-white px-4 py-2 rounded font-semibold hover:bg-green-700 transition"
            onClick={handleSave}
          >
            Save Record
          </button>
        </div>
      </div>
    </Modal>
  );
};

export default RecordModal;
