import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import Layout from "../components/Layout";

const OrgHomePage = () => {
  const { orgId } = useParams(); // Get the organization ID from the URL
  const [organizationDetails, setOrganizationDetails] = useState(null); // Store organization details
  const [enabledComponents, setEnabledComponents] = useState([]); // Store enabled components for the user
  const [error, setError] = useState("");
  const token = localStorage.getItem("token"); // Retrieve the token from localStorage
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        const response = await fetch("http://localhost:4000/api/organizations/user-details", {
          headers: { Authorization: `Bearer ${token}` },
        });
        const data = await response.json();

        if (response.ok) {
          // Find the specific organization
          const org = data.userDetails.find(
            (org) => org.organizationId === orgId
          );

          if (!org) {
            setError("You are not a member of this organization.");
            return;
          }

          setOrganizationDetails(org);
          const accessibleComponents = org.components
            .filter((comp) => comp.enabled && comp.hasAccess)
            .map((comp) => comp.componentName);
          setEnabledComponents(accessibleComponents);
        } else {
          setError(data.message || "Failed to fetch user details.");
        }
      } catch (error) {
        console.error("Error fetching user details:", error);
        setError("Something went wrong. Please try again later.");
      }
    };

    fetchUserDetails();
  }, [orgId, token]);

  if (error) {
    return (
      <Layout>
        <p className="text-red-500 text-center bg-red-100 p-3 rounded">
          {error}
        </p>
      </Layout>
    );
  }

  if (!organizationDetails) {
    return (
      <Layout>
        <p className="text-gray-500 text-center">Loading...</p>
      </Layout>
    );
  }

  const { organizationName, role } = organizationDetails;

  const renderButton = (component, label, navigateTo) => {
    const isEnabled = enabledComponents.includes(component);
    return (
      <button
        className={`px-6 py-3 rounded-lg font-semibold transition ${
          isEnabled
            ? "bg-blue-600 text-white hover:bg-blue-700"
            : "bg-gray-400 text-gray-800 cursor-not-allowed"
        }`}
        onClick={() => isEnabled && navigate(navigateTo)}
        disabled={!isEnabled}
      >
        {isEnabled
          ? label
          : `${label} (Contact admin to enable this feature)`}
      </button>
    );
  };

  return (
    <Layout>
      <div className="min-h-screen bg-gradient-to-r from-blue-500 to-purple-500 p-10 text-white">
        <div className="max-w-5xl mx-auto bg-white p-8 rounded-lg shadow-lg text-gray-800">
          <h1 className="text-4xl font-bold mb-6 text-center">{organizationName}</h1>

          {/* Admin Controls */}
          {role === "admin" && (
            <div className="mt-6 text-center">
              <button
                className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700 transition font-semibold"
                onClick={() => navigate(`/organization/${orgId}/admin`)}
              >
                Admin Controls
              </button>
            </div>
          )}

          {/* Component Buttons */}
          <div className="mt-6 space-y-4">
            {renderButton(
              "finance",
              "Go to Finance Management",
              `/organization/${orgId}/finance`
            )}
            {renderButton(
              "tasks",
              "Go to Task Management",
              `/organization/${orgId}/tasks`
            )}
            {renderButton(
              "documentation",
              "Go to Documentation",
              `/organization/${orgId}/documentation`
            )}
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default OrgHomePage;
