import React from 'react';

const Layout = ({ children }) => {
  return (
    <div className="bg-gradient-to-r from-blue-500 to-purple-500 min-h-screen text-white">
      <header className="flex justify-between items-center p-6">
        <h1 className="text-3xl font-bold">ProjectX</h1>
        <nav className="space-x-4">
          <a href="/login" className="bg-white text-blue-600 px-4 py-2 rounded">Login</a>
          <a href="/register" className="bg-blue-600 px-4 py-2 rounded">Sign Up</a>
        </nav>
      </header>
      <main className="flex flex-col justify-center items-center text-center mt-10">
        {children}
      </main>
    </div>
  );
};

export default Layout;
