import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Layout from "../components/Layout";

const OrgHomePage = () => {
  const { orgId } = useParams();
  const [organization, setOrganization] = useState(null);
  const [error, setError] = useState("");

  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchOrganizationDetails = async () => {
      try {
        const response = await fetch(
          `http://localhost:4000/api/organizations/${orgId}`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        const data = await response.json();
        if (response.ok) {
          setOrganization(data.organization);
        } else {
          setError(data.message || "Failed to fetch organization details.");
        }
      } catch (error) {
        console.error("Error fetching organization details:", error);
        setError("Something went wrong. Please try again later.");
      }
    };

    fetchOrganizationDetails();
  }, [orgId, token]);

  if (error) {
    return (
      <Layout>
        <p className="text-red-500 text-center bg-red-100 p-3 rounded">
          {error}
        </p>
      </Layout>
    );
  }

  if (!organization) {
    return (
      <Layout>
        <p className="text-gray-500 text-center">Loading...</p>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className="p-8 space-y-6">
        <h1 className="text-4xl font-extrabold text-center text-white-600">
          {organization.name}
        </h1>
        <div className="bg-white p-6 rounded shadow-lg space-y-4">
          <p><strong>Address:</strong> {organization.address || "N/A"}</p>
          <p><strong>Contact Number:</strong> {organization.contactNumber || "N/A"}</p>
          <p><strong>Email:</strong> {organization.email || "N/A"}</p>
          <p><strong>Description:</strong> {organization.description || "No description available."}</p>
          <p><strong>Industry:</strong> {organization.industry || "N/A"}</p>
          <p><strong>Website:</strong> {organization.website || "N/A"}</p>
          <p><strong>Registration:</strong> {organization.registrationId || "N/A"}</p>
        </div>
        {organization.isAdmin && (
          <button
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
          >
            Admin Controls
          </button>
        )}
      </div>
    </Layout>
  );
};

export default OrgHomePage;
