import React, { useState, useEffect } from "react";
import Layout from "../components/Layout";
import Modal from "../components/Modal";
import { useNavigate } from "react-router-dom";

const FinanceManagement = () => {
  const [activeTab, setActiveTab] = useState("categories");
  const [categories, setCategories] = useState([]);
  const [records, setRecords] = useState([]);
  const [fieldDefinitions, setFieldDefinitions] = useState([]);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [modalContent, setModalContent] = useState(null);

  const token = localStorage.getItem("token");
  const orgId = localStorage.getItem("currentOrgId");

  const navigate = useNavigate();

  useEffect(() => {
    if (!orgId) {
      setError("Organization ID is missing. Redirecting to organization selection...");
      setTimeout(() => navigate("/dashboard"), 3000); // Redirect after 3 seconds
    } else {
      if (activeTab === "categories") fetchCategories();
      else if (activeTab === "records") fetchRecords();
      else if (activeTab === "fields") fetchFieldDefinitions();
    }
  }, [activeTab]);

  const fetchCategories = async () => {
    if (!orgId) return;
    try {
      setLoading(true);
      const response = await fetch(
        `http://localhost:4000/api/organizations/${orgId}/components/finance/categories`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      const data = await response.json();
      if (response.ok) setCategories(data.categories || []);
      else setError(data.message || "Failed to fetch categories.");
    } catch (err) {
      setError("Error fetching categories.");
    } finally {
      setLoading(false);
    }
  };

  const fetchRecords = async () => {
    if (!orgId) return;
    try {
      setLoading(true);
      const response = await fetch(
        `http://localhost:4000/api/organizations/${orgId}/components/finance/records`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      const data = await response.json();
      if (response.ok) setRecords(data.records || []);
      else setError(data.message || "Failed to fetch records.");
    } catch (err) {
      setError("Error fetching records.");
    } finally {
      setLoading(false);
    }
  };

  const fetchFieldDefinitions = async () => {
    if (!orgId) return;
    try {
      setLoading(true);
      const response = await fetch(
        `http://localhost:4000/api/organizations/${orgId}/components/finance/fields`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      const data = await response.json();
      if (response.ok) setFieldDefinitions(data.fields || []);
      else setError(data.message || "Failed to fetch field definitions.");
    } catch (err) {
      setError("Error fetching field definitions.");
    } finally {
      setLoading(false);
    }
  };

  const handleModalClose = () => {
    setShowModal(false);
    setModalContent(null);
  };

  return (
    <Layout>
      <div className="min-h-screen bg-gradient-to-r from-blue-500 to-purple-500 text-white p-8">
        <h1 className="text-4xl font-extrabold text-center mb-8">
          Finance Management
        </h1>
        {error && <p className="text-red-500">{error}</p>}

        {/* Tabs */}
        <div className="flex justify-center space-x-6 mb-6">
          <button
            className={`px-6 py-2 rounded ${
              activeTab === "categories" ? "bg-blue-600 text-white" : "bg-white text-blue-600"
            }`}
            onClick={() => setActiveTab("categories")}
          >
            Categories
          </button>
          <button
            className={`px-6 py-2 rounded ${
              activeTab === "records" ? "bg-blue-600 text-white" : "bg-white text-blue-600"
            }`}
            onClick={() => setActiveTab("records")}
          >
            Records
          </button>
          <button
            className={`px-6 py-2 rounded ${
              activeTab === "fields" ? "bg-blue-600 text-white" : "bg-white text-blue-600"
            }`}
            onClick={() => setActiveTab("fields")}
          >
            Fields
          </button>
        </div>

        {/* Tab Content */}
        {loading ? (
          <p>Loading...</p>
        ) : (
          <div>
            {activeTab === "categories" && (
              <div>
                <h2 className="text-2xl font-bold mb-4">Categories</h2>
                <button
                  className="bg-green-600 px-4 py-2 rounded mb-4"
                  onClick={() => setShowModal(true)}
                >
                  Add Category
                </button>
                <ul>
                  {categories.map((category) => (
                    <li key={category._id}>{category.name}</li>
                  ))}
                </ul>
              </div>
            )}
            {activeTab === "records" && (
              <div>
                <h2 className="text-2xl font-bold mb-4">Records</h2>
                <button
                  className="bg-green-600 px-4 py-2 rounded mb-4"
                  onClick={() => setShowModal(true)}
                >
                  Add Record
                </button>
                <ul>
                  {records.map((record) => (
                    <li key={record._id}>{record.type}</li>
                  ))}
                </ul>
              </div>
            )}
            {activeTab === "fields" && (
              <div>
                <h2 className="text-2xl font-bold mb-4">Field Definitions</h2>
                <button
                  className="bg-green-600 px-4 py-2 rounded mb-4"
                  onClick={() => setShowModal(true)}
                >
                  Add Field
                </button>
                <ul>
                  {fieldDefinitions.map((field) => (
                    <li key={field._id}>{field.name}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        )}

        {/* Modal */}
        {showModal && (
          <Modal onClose={handleModalClose}>
            <div>
              <h2>Add Item</h2>
              {/* Form Content */}
              <button onClick={handleModalClose}>Close</button>
            </div>
          </Modal>
        )}
      </div>
    </Layout>
  );
};

export default FinanceManagement;
