import React, { useState, useEffect } from "react";
import Layout from "../components/Layout";

const AdminControlPage = ({ orgId }) => {
  const [components, setComponents] = useState([]);
  const [organizationComponents, setOrganizationComponents] = useState([]);
  const [accessRequests, setAccessRequests] = useState([]);
  const [users, setUsers] = useState([]);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!orgId) return; // Prevent calls if orgId is undefined
    fetchComponents();
    fetchOrgComponents();
    fetchAccessRequests();
    fetchOrgUsers();
  }, [orgId]);

  const fetchComponents = async () => {
    try {
      const response = await fetch(`http://localhost:4000/api/components`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await response.json();
      setComponents(data.components || []);
    } catch (error) {
      setError("Failed to fetch components.");
    }
  };

  const fetchOrgComponents = async () => {
    try {
      const response = await fetch(
        `http://localhost:4000/api/organizations/${orgId}/components`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      const data = await response.json();
      setOrganizationComponents(data.components || []);
    } catch (error) {
      setError("Failed to fetch organization components.");
    }
  };

  const fetchAccessRequests = async () => {
    try {
      const response = await fetch(
        `http://localhost:4000/api/organizations/${orgId}/access-requests`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      const data = await response.json();
      setAccessRequests(data.requests || []);
    } catch (error) {
      setError("Failed to fetch access requests.");
    }
  };

  const fetchOrgUsers = async () => {
    try {
      const response = await fetch(
        `http://localhost:4000/api/organizations/${orgId}/users`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      const data = await response.json();
      setUsers(data.users || []);
    } catch (error) {
      setError("Failed to fetch organization users.");
    }
  };

  const handleEnableComponent = async (componentName) => {
    setLoading(true);
    try {
      await fetch(
        `http://localhost:4000/api/organizations/${orgId}/components/${componentName}/enable`,
        {
          method: "POST",
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      fetchOrgComponents();
    } catch (error) {
      setError("Failed to enable component.");
    }
    setLoading(false);
  };

  const handleAccessRequest = async (requestId, action) => {
    setLoading(true);
    try {
      await fetch(
        `http://localhost:4000/api/organizations/${orgId}/access-requests/${requestId}/${action}`,
        {
          method: "POST",
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      fetchAccessRequests();
    } catch (error) {
      setError(`Failed to ${action} access request.`);
    }
    setLoading(false);
  };

  const handleChangeUserRole = async (userId, role) => {
    setLoading(true);
    try {
      await fetch(
        `http://localhost:4000/api/organizations/${orgId}/users/${userId}/role`,
        {
          method: "PUT",
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ role }),
        }
      );
      fetchOrgUsers();
    } catch (error) {
      setError("Failed to change user role.");
    }
    setLoading(false);
  };

  const handleRemoveUser = async (userId) => {
    setLoading(true);
    try {
      await fetch(
        `http://localhost:4000/api/organizations/${orgId}/users/${userId}`,
        {
          method: "DELETE",
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      fetchOrgUsers();
    } catch (error) {
      setError("Failed to remove user.");
    }
    setLoading(false);
  };

  return (
    <Layout>
      <div className="p-8 space-y-8 min-h-screen bg-gradient-to-r from-blue-500 to-purple-500 text-white">
        <h1 className="text-4xl font-extrabold text-center">Admin Controls</h1>

        {error && <p className="text-red-500 bg-red-100 p-3 rounded">{error}</p>}

        {/* Assign Components */}
        <div className="bg-white p-6 rounded shadow-lg">
          <h2 className="text-2xl font-bold text-gray-800 mb-4">
            Assign Components
          </h2>
          <div className="grid grid-cols-2 gap-4">
            {components?.map((component) => (
              <button
                key={component.name}
                className={`py-2 px-4 rounded ${
                  organizationComponents?.some(
                    (c) => c.componentName === component.name
                  )
                    ? "bg-gray-400 cursor-not-allowed"
                    : "bg-green-600 hover:bg-green-700"
                } text-white font-semibold`}
                onClick={() => handleEnableComponent(component.name)}
                disabled={organizationComponents?.some(
                  (c) => c.componentName === component.name
                )}
              >
                {component.name}
              </button>
            ))}
          </div>
        </div>

        {/* Access Requests */}
        <div className="bg-white p-6 rounded shadow-lg">
          <h2 className="text-2xl font-bold text-gray-800 mb-4">
            Access Requests
          </h2>
          {accessRequests?.length === 0 ? (
            <p className="text-gray-500">No pending access requests.</p>
          ) : (
            <div className="space-y-4">
              {accessRequests?.map((request) => (
                <div
                  key={request._id}
                  className="flex justify-between items-center p-4 border rounded shadow"
                >
                  <div>
                    <p>
                      <strong>{request.userId?.firstName}</strong> (
                      {request.userId?.email})
                    </p>
                  </div>
                  <div className="space-x-4">
                    <button
                      className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
                      onClick={() => handleAccessRequest(request._id, "approve")}
                    >
                      Approve
                    </button>
                    <button
                      className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
                      onClick={() => handleAccessRequest(request._id, "reject")}
                    >
                      Reject
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* User Management */}
        <div className="bg-white p-6 rounded shadow-lg">
          <h2 className="text-2xl font-bold text-gray-800 mb-4">
            Manage Users
          </h2>
          {users?.map((user) => (
            <div
              key={user._id}
              className="flex justify-between items-center p-4 border rounded shadow"
            >
              <div>
                <p>
                  <strong>
                    {user.firstName} {user.lastName}
                  </strong>{" "}
                  ({user.email})
                </p>
                <p>Role: {user.role}</p>
              </div>
              <div className="space-x-4">
                <button
                  className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
                  onClick={() => handleChangeUserRole(user._id, "admin")}
                >
                  Make Admin
                </button>
                <button
                  className="bg-yellow-600 text-white px-4 py-2 rounded hover:bg-yellow-700"
                  onClick={() => handleChangeUserRole(user._id, "member")}
                >
                  Make Member
                </button>
                <button
                  className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
                  onClick={() => handleRemoveUser(user._id)}
                >
                  Remove
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </Layout>
  );
};

export default AdminControlPage;
